import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-new-listing-page',
  templateUrl: './new-listing-page.component.html',
  styleUrls: ['./new-listing-page.component.css']
})
export class NewListingPageComponent {

  name: string = '';
  description: string = '';
  price: string = ''
  constructor(
    private router: Router,
  ) {}

  ngOnInit(): void {

  }

  onSubmit(): void {
    alert('Creating a new listing....');
    this.router.navigateByUrl('/my-listings');
  }
}
<p>new-listing-page works!</p>
<div class="content-box">
  <h2>Create New Listing</h2>
  <form (ngSubmit)="onSubmit()">
    <div>
      <label for="name">
        Name:
      </label>
      <input id="name" name="name" type="text" [(ngModel)]="name" )]/>
    </div>
    <div>
      <label for="description">
        Item description:
      </label>
      <input id="description" name="description" type="text" [(ngModel)]="description" )]/>
    </div>
    <div>
      <label for="price">
        Price:
      </label>
      <input id="price" name="price" type="text" [(ngModel)]="price" )]/>
    </div>

  </form>
</div>
