import { Component } from '@angular/core';
import { fakeMyListings } from "../fake-data";
import { Listing } from "../types";

@Component({
  selector: 'app-my-listings-page',
  templateUrl: './my-listings-page.component.html',
  styleUrls: ['./my-listings-page.component.css']
})
export class MyListingsPageComponent {
  listings: Listing[] = [];

  constructor() {}

  ngOnInit(): void {
    this.listings = fakeMyListings;
  }

  onDeleteClicked(listingId: string): void {
    alert(`Deleting your listing with id ${listingId}`);
  }

}
<p>my-listings-page works!</p>
<div class="content-box" *ngFor="let listing of listings">
  <h3>{{listing.name}}</h3>
  <p>{{listing.description}}</p>
  <p>${{listing.price}}</p>
  <div class="buttons-container">
    <a routerLink="/edit-listing/{{listing.id}}">
      <button>Edit</button>
    </a>
    <button (click)="onDeleteClicked(listing.id)">Delete</button>
  </div>
</div>
