import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Listing } from '../types';
import {fakeListings, fakeMyListings} from "../fake-data";

@Component({
  selector: 'app-edit-listing-page',
  templateUrl: './edit-listing-page.component.html',
  styleUrls: ['./edit-listing-page.component.css']
})
export class EditListingPageComponent {
  listing: Listing | undefined;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
  ){ }

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    this.listing = fakeMyListings.find(listing => listing.id === id);
  }

  onSubmit(): void {

  }

}
<p>edit-listing-page works!</p>
<div class="content-box">
  <h2>Edit Listing</h2>
  <app-listing-data-form
    buttonText="Save Changes"
    [currentName]="listing.name"
    [currentDescription]="listing.description"
    [currentPrice]="listing.price"
    (onSubmit)="onSubmit()" )></app-listing-data-form>
</div>
