import { Component, OnInit, Input,Output, EventEmitter } from '@angular/core';
import { Router } from '@angular/router';
import { Listing } from '../types';
@Component({
  selector: 'app-listing-data-form',
  templateUrl: './listing-data-form.component.html',
  styleUrls: ['./listing-data-form.component.css']
})
export class ListingDataFormComponent {
  @Input() buttonText:any;
  @Input() currentName ='';
  @Input() currentDescription ='';
  @Input() currentPrice:any;
  name: string ='';
  description: string ='';
  price: string ='';
  @Output() onSubmit = new EventEmitter<Listing>();

  constructor(
    private router: Router,
  ) { }

  ngOnInit(): void {
    this.name = this.currentName;
    this.description = this.currentDescription;
    this.price = this.currentPrice;
  }
  onButtonClicked(): void {
    this.onSubmit.emit({
      id: '',
      name: this.name,
      description: this.description,
      price: Number(this.price),
    });

  }
}

  <form (ngSubmit)="onButtonClicked()">
    <div>
      <label for="name">
        Name:
      </label>
      <input id="name" name="name" type="text" [(ngModel)]="name"/>
    </div>
    <div>
      <label for="description">
        Item description:
      </label>
      <input id="description" name="description" type="text" [(ngModel)]="description"/>
    </div>
    <div>
      <label for="price">
        Price:
      </label>
      <input id="price" name="price" type="text" [(ngModel)]="price"/>
    </div>
    <button type="submit">{{buttonText}}</button>
  </form>
